import React from "react";
import { Container, Col, Row, Button } from "react-bootstrap";

const productsArr = [
  {
    title: "Album 1",

    price: 100,

    imageUrl: "https://prasadyash2411.github.io/ecom-website/img/Album%201.png",
  },

  {
    title: "Album 2",

    price: 50,

    imageUrl: "https://prasadyash2411.github.io/ecom-website/img/Album%202.png",
  },

  {
    title: "Album 3",

    price: 70,

    imageUrl: "https://prasadyash2411.github.io/ecom-website/img/Album%203.png",
  },

  {
    title: "Album 4",

    price: 100,

    imageUrl: "https://prasadyash2411.github.io/ecom-website/img/Album%204.png",
  },
];
const ProductList = () => {
  return (
    <div>
      <div style={{background:"grey", height:"170px", textAlign:"center" ,fontSize:"200px",color:"white",fontFamily:"cursive"}}>
        <div className="display-1 fw-bold mt-1">The Generics</div>
      </div>
      <Container style={{ width: "1000px" }}>
        <h2 style={{textAlign:"center" , marginTop:"60px", fontFamily:"cursive"}}> Music </h2>
        <Row>
          {productsArr.map((item) => (
            <Col className="col-6 ">
              <div className=" mt-4 d-flex flex-column align-items-center  ">
                <h3>{item.title}</h3>
                <img src={item.imageUrl} alt={item.title} width="250px" />
                <div
                  className="d-flex justify-content-between bg-white mt-2"
                  style={{ width: "300px" }}
                >
                  <h6 className="mr-3 mt-3">
                    {`\u20B9`}
                    {item.price}
                  </h6>
                  <Button variant="success" className="mr-3">
                    Add to Cart
                  </Button>
                </div>
              </div>
            </Col>
          ))}
        </Row>
      </Container>
    </div>
  );
};

export default ProductList;
